              {/* require(`../images/postImages/${post.img}`) */}
              {/* src={require(`../images/postImages/${props.props.postImage}`)} */}
              {/* /var/www/html/my_projects/React/blog_app_react/client/src/images/postImages/1655480999876_bt21.jpg */}
